import NewsCard from "../components/NewsCard.vue";
export default {
  name: "HomeView",
  components: {
    NewsCard: NewsCard
  },
  data: function data() {
    return {
      news: []
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.axios.get("https://newsapi.org/v2/top-headlines?country=ru&apiKey=d7f41a32c26b4bbfb596d58b1a54c766").then(function (news) {
      for (var i in news.data.articles) {
        _this.news.push(news.data.articles[i]);
      }
    });

    for (var i in this.news) {
      var item = this.news[i];

      if (item.urlToImage == null || item.title == null || item.description == null) {
        this.news.splice(i, i);
      }
    }
  }
};
